package com.yxw.framework.common.cache;

/**
 * 缓存数据配置类
 * 
 * @author 申午武
 * @version 1.0
 * @since 2015年3月4日
 */

public class CacheConfig {
    /**
     * 固定缓存
     */
    private boolean fixedCacheStop = false;
    /**
     * 即时缓存
     */
    private boolean instantCacheStop = true;
    /**
     * 接口缓存
     */
    private boolean methodCacheStop = true;
    
    /**
     * @return the fixedCacheStop
     */
    
    public boolean isFixedCacheStop() {
        return fixedCacheStop;
    }
    
    /**
     * @param fixedCacheStop
     *            the fixedCacheStop to set
     */
    
    public void setFixedCacheStop(boolean fixedCacheStop) {
        this.fixedCacheStop = fixedCacheStop;
    }
    
    /**
     * @return the instantCacheStop
     */
    
    public boolean isInstantCacheStop() {
        return instantCacheStop;
    }
    
    /**
     * @param instantCacheStop
     *            the instantCacheStop to set
     */
    
    public void setInstantCacheStop(boolean instantCacheStop) {
        this.instantCacheStop = instantCacheStop;
    }
    
    /**
     * @return the methodCacheStop
     */
    
    public boolean isMethodCacheStop() {
        return methodCacheStop;
    }
    
    /**
     * @param methodCacheStop
     *            the methodCacheStop to set
     */
    
    public void setMethodCacheStop(boolean methodCacheStop) {
        this.methodCacheStop = methodCacheStop;
    }
    
}
